import { Autocomplete, Box, IconButton, Stack, TextField } from "@mui/material";
import React, { Fragment, useEffect, useState } from "react";
import SearchIcon from "@mui/icons-material/Search";
import "../App.css";
import { Main } from "./Main";

const Search = () => {
  const [result, setResult] = useState([]);
  const [open, setOpen] = useState(false);
  const [first, setFirst] = useState("");
  const [word, setWord] = useState("");
  const [isValueSelected, setIsValueSelected] = useState(false);

  function handleChange(e) {
    setWord(e.target.value);
  }

  useEffect(() => {
    const fetchBooks = async () => {
      try {
        if (word) {
          const response = await fetch(`/codes/${word}/matches`);
          if (response.ok) {
            const data = await response.json();
            setResult(data);
          } else {
            console.error("Failed to fetch data");
          }
        } else {
          setResult([]);
        }
      } catch (error) {
        console.error("Error:", error);
      }
    };
    fetchBooks();
  }, [word]);

  global.values = first;

  return (
    <>
      <Box
        sx={{
          height: "80px",
          position: "static",
        }}
      >
        <Stack
          sx={{ margin: "auto", color: "black", mt: "20px" }}
          direction={"row"}
          gap={5}
        >
          <Fragment>
            <Autocomplete
              id="users"
              defaultValue={null}
              getOptionLabel={(result) => `${result.id} ${result.description}`}
              options={result}
              sx={{
                width: "1450px",
                backgroundColor: "white",
                mt: "14px",
                ml: "130px",
                "& .MuiAutocomplete-inputRoot": {
                  "& input": {
                    height: "5px",
                    bgcolor: "background.paper",
                    color: (theme) =>
                      theme.palette.getContrastText(
                        theme.palette.background.paper
                      ),
                  },
                },
              }}
              isOptionEqualToValue={(option, value) =>
                option.description === value.description
              }
              noOptionsText={"PLEASE ENTER VALID CODES"}
              open={open}
              onOpen={() => setOpen(true)}
              onClose={() => setOpen(false)}
              onInputChange={(event, value) => {
                setWord(value);
                if (value.length === 0) {
                  setOpen(false);
                } else {
                  setOpen(true);
                }
              }}
              popupIcon={
                <IconButton
                  disableRipple
                  onClick={() => setOpen(!open)}
                  sx={{
                    "&:hover": { cursor: "default" },
                  }}
                >
                  <SearchIcon />
                </IconButton>
              }
              onChange={(event, newValue) => {
                setFirst(newValue);
                setIsValueSelected(true);
              }}
              autoSelect
              renderOption={(props, result) => (
                <Box {...props} key={result.id}>
                  {result.id} {result.description}
                </Box>
              )}
              renderInput={(params) => (
                <TextField
                  {...params}
                  onChange={handleChange}
                  placeholder="Search for code"
                />
              )}
            />
          </Fragment>
        </Stack>
      </Box>
      <Main isValueSelected={isValueSelected} />
    </>
  );
};

export default Search;
