import { Autocomplete, Box, IconButton, Stack, TextField } from "@mui/material";
import React, { Fragment, useEffect, useState } from "react";
import SearchIcon from "@mui/icons-material/Search";
import CloseIcon from "@mui/icons-material/Close";
import "../App.css";
import { Main } from "./Main";

const Search = () => {
  const [result, setResult] = useState([]);
  const [open, setOpen] = useState(false);
  const [first, setFirst] = useState("");
  const [word, setWord] = useState("");
  const [isValueSelected, setIsValueSelected] = useState(false);

  function handleChange(e) {
    setWord(e.target.value);
  }
  console.log(word);

  useEffect(() => {
    const fetchBooks = async () => {
      try {
        if (word) {
          const response = await fetch(`/codes/${word}/matches`);
          if (response.ok) {
            const data = await response.json();
            setResult(data);
          } else {
            console.error("Failed to fetch data");
          }
        } else {
          setResult([]);
        }
      } catch (error) {
        console.error("Error:", error);
      }
    };
    fetchBooks();
  }, [word]);
  console.log("our result is", result);
  console.log(first);
  global.values = first;
  console.log(global.values && global.values.code);
  return (
    <>
      <Box
        sx={{
          height: "80px",
          position: "static",
        }}
      >
        <Stack
          sx={{ margin: "auto", color: "black", mt: "20px" }}
          direction={"row"}
          gap={5}
        >
          <Fragment>
            <Autocomplete
              id="users"
              defaultValue={null}
              getOptionLabel={(result) => `${result.id} ${result.description}`}
              options={result}
              sx={{
                width: "1450px",
                backgroundColor: "white",
                mt: "14px",
                ml: "130px",
                display: "inline-block",
                "& input": {
                  height: "5px",
                  bgcolor: "background.paper",
                  color: (theme) =>
                    theme.palette.getContrastText(
                      theme.palette.background.paper
                    ),
                },
              }}
              isOptionEqualToValue={(option, value) =>
                option.description === value.description
              }
              noOptionsText={"PLEASE ENTER VALID CODES"}
              open={open}
              onOpen={() => setOpen(true)} // Add onOpen handler
              onClose={() => setOpen(false)}
              renderInput={(params) => (
                <TextField
                  {...params}
                  onChange={handleChange}
                  placeholder="Search for code"
                  InputProps={{
                    ...params.InputProps,
                    endAdornment: (
                      <React.Fragment>
                        {isValueSelected && (
                          <IconButton
                            aria-label="clear"
                            onClick={() => {
                              setFirst("");
                              setIsValueSelected(false);
                            }}
                            onMouseDown={(event) => {
                              event.preventDefault();
                            }}
                            size="small"
                            sx={{
                              "&:hover": { background: "none", boxShadow: "none" },
                            }}
                          >
                            <CloseIcon />
                          </IconButton>
                        )}
                      </React.Fragment>
                    ),
                    startAdornment: (
                      <IconButton
                        aria-label="search"
                        onClick={() => {
                          if (params.inputProps.value) {
                            setOpen(true);
                          }
                        }}
                        size="small"
                        sx={{
                          "&:hover": { background: "none", boxShadow: "none" },
                        }}
                      >
                        <SearchIcon />
                      </IconButton>
                    ),
                  }}
                />
              )}
            />
          </Fragment>
        </Stack>
      </Box>
      <Main isValueSelected={isValueSelected} />
    </>
  );
};

export default Search;
