import React, { useEffect, useState } from "react";

const Chapternotes = () => {
  const [results, setResults] = useState("");

  useEffect(() => {
    const fetchChapterDetails = async () => {
      try {
        const response = await fetch(`/chapters/${global.values.chapter}/details`);
        if (response.ok) {
          const data = await response.json();
          setResults(data);
        } else {
          console.error("Failed to fetch data");
        }
      } catch (error) {
        console.error("Error:", error);
      }
    };
    fetchChapterDetails();
  }, [global.values.chapter]);

  console.log("our result is", results);

  return (
    <div className="chapternotes">
      <div>
        <table>
          <thead>
            <tr></tr>
          </thead>
          <tbody>
            {results && results.description !== null ? (
              <tr key={results.chapter}>
                <td>{results.description}</td>
              </tr>
            ) : (
              <tr>
                <td>No description available.</td>
              </tr>
            )}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default Chapternotes;
