import { Autocomplete, Box, TextField } from "@mui/material";
import React, { Fragment, useEffect, useState } from "react";
import SearchIcon from "@mui/icons-material/Search";
import "../App.css";
import { Main } from "./Main";

const Search = () => {
  const [result, setResult] = useState([]);
  const [open, setOpen] = useState(false);
  const [first, setFirst] = useState("");
  const [word, setWord] = useState("");
  const [isValueSelected, setIsValueSelected] = useState(false);

  function handleChange(e) {
    setWord(e.target.value);
  }

  useEffect(() => {
    const fetchBooks = async () => {
      try {
        if (word) {
          const response = await fetch(`/codes/${word}/matches`);
          if (response.ok) {
            const data = await response.json();
            setResult(data);
          } else {
            console.error("Failed to fetch data");
          }
        } else {
          setResult([]);
        }
      } catch (error) {
        console.error("Error:", error);
      }
    };
    fetchBooks();
  }, [word]);

  const handleInputChange = (_, value) => {
    if (value.length === 0) {
      if (open) setOpen(false);
    } else {
      if (!open) setOpen(true);
    }
  };

  const handleAutocompleteChange = (event, newValue) => {
    setFirst(newValue);
    setIsValueSelected(true);
  };

  return (
    <>
      <Box sx={{ height: "80px", position: "static" }}>
        <Autocomplete
          id="users"
          defaultValue={null}
          getOptionLabel={(result) => `${result.id} ${result.description}`}
          options={result}
          sx={{
            width: "1450px",
            backgroundColor: "white",
            mt: "14px",
            ml: "130px",
            display: "inline-block",
            "& input": {
              height: "5px",
              bgcolor: "background.paper",
              color: (theme) =>
                theme.palette.getContrastText(theme.palette.background.paper),
            },
          }}
          isOptionEqualToValue={(option, value) =>
            option.description === value.description
          }
          noOptionsText={"PLEASE ENTER VALID CODES"}
          open={open}
          onInputChange={handleInputChange}
          onClose={() => setOpen(false)}
          popupIcon={
            <SearchIcon sx={{ "&:hover": { background: "none" } }} />
          }
          onChange={handleAutocompleteChange}
          autoSelect
          renderOption={(props, result) => (
            <Box {...props} key={result.id}>
              {result.id} {result.description}
            </Box>
          )}
          renderInput={(params) => (
            <TextField
              {...params}
              onChange={handleChange}
              placeholder="Search for code"
            />
          )}
        />
      </Box>
      <Main isValueSelected={isValueSelected} />
      {result.length === 0 && <div>No results found</div>}
    </>
  );
};

export default Search;
